create database if not exists `db_tiger` /*!40100 DEFAULT CHARACTER SET utf8 */;
use `db_tiger`;
create table if not exists `tb_union_list` (  `gprs_id` varchar(20) NOT NULL,  `gprs_stat` int(11) DEFAULT NULL,  `time_online` varchar(45) DEFAULT NULL,  `alias` varchar(45) DEFAULT NULL,  PRIMARY KEY (`gprs_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
delete from `tb_union_list`;
create table if not exists `tb_unit_para` (  `UnitId` int(11) NOT NULL,  `Aera_IrradiatedSum` varchar(20) DEFAULT NULL COMMENT '集热器面积',  `Auxiliary_power` varchar(20) DEFAULT NULL COMMENT '辅助热源功率',  `Flow_CollectorSys` varchar(20) DEFAULT NULL COMMENT '集热系统进出口流量',  `Flow_HeatUsing` varchar(20) DEFAULT NULL COMMENT '热用户端出水流量',  `Aperture_Area` varchar(20) DEFAULT NULL COMMENT '太阳能集热器采光面积',  PRIMARY KEY (`UnitId`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
delete from `tb_unit_para`;
create table if not exists `tb_unit_state` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `Temp_HeatingBox` varchar(20) DEFAULT NULL COMMENT '供热水箱温度',  `Temp_CollectorBox` varchar(20) DEFAULT NULL COMMENT '集热水箱温度',  `Temp_CollectorIn` varchar(20) DEFAULT NULL COMMENT '集热系统进口温度',  `Temp_CollectorOut` varchar(20) DEFAULT NULL COMMENT '集热系统出口温度',  `Temp_Ambient` varchar(20) DEFAULT NULL COMMENT '环境温度',  `Humidity_Ambient` varchar(20) DEFAULT NULL COMMENT '环境湿度',  `Speed_Wind` varchar(20) DEFAULT NULL COMMENT '风速',  `SystemState` varchar(20) DEFAULT NULL COMMENT '系统状态',  `ErrorState` varchar(20) DEFAULT NULL COMMENT '系统故障状态',  `UnitId` int(11) DEFAULT NULL COMMENT '用户单元ID',  `DateTime RecvDate` varchar(20) DEFAULT NULL COMMENT '数据采集时间',  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
delete from `tb_unit_state`;
create table if not exists `tb_unit_statistics` (  `UnitId` int(11) NOT NULL,  `System_heat` varchar(20) DEFAULT NULL COMMENT '集热系统得热量',  `Conventional_energy` varchar(20) DEFAULT NULL COMMENT '系统常规热源耗能量',  `Storage_tank` varchar(20) DEFAULT NULL COMMENT '贮热水箱热损系数',  `System_efficiency` varchar(20) DEFAULT NULL COMMENT '集热系统效率',  `Solar_assurance_day` varchar(20) DEFAULT NULL COMMENT '日太阳能保证率',  `Solar_assurance_year` varchar(20) DEFAULT NULL COMMENT '全年太阳能保证率',  `Energy_alternative` varchar(20) DEFAULT NULL COMMENT '常规能源替代量',  `Carbon_emission` varchar(20) DEFAULT NULL COMMENT '二氧化碳减排量',  `Sulfur_emission` varchar(20) DEFAULT NULL COMMENT '二氧化硫减排量',  `Dust_emission` varchar(20) DEFAULT NULL COMMENT '粉尘减排量',  `Fee_effect` varchar(20) DEFAULT NULL COMMENT '项目费效比',  PRIMARY KEY (`UnitId`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
delete from `tb_unit_statistics`;
