create database if not exists `db_tiger` /*!40100 DEFAULT CHARACTER SET utf8 */;
use `db_tiger`;
create table if not exists `tb_union_list` (  `UnitId` varchar(20) NOT NULL,  `gprs_state` int(11) DEFAULT NULL,  `alias` varchar(45) NOT NULL,  `Aera_IrradiatedSum` int(11) NOT NULL COMMENT '集热器面积',  `Volumn_HeatingBox` int(11) NOT NULL COMMENT '贮热水箱容量',  `Start_time` datetime DEFAULT NULL,  `Stop_time` datetime DEFAULT NULL,  `time_online` int(11) DEFAULT NULL,  PRIMARY KEY (`UnitId`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
delete from `tb_union_list`;
